import React, { Component } from "react";
import resuCard from "../components/resuCard"
import API from "../utils/API"
import Wrapper from "../components/Wrapper"

class Portfolio extends Component{

  state = {
results: [],
API  };
componentDidMount() {
  this.loadresus();
}


loadresus = () => {
  API.getresus()
    .then(res => this.setState({ resus: res.data }))
    .catch(err => console.log(err));
};




render(){
return(
  <Wrapper>
  {this.state.results.map(result => (
    <resuCard
      id={this.state.resu.id}
      key={this.state.resu.id}
      name={this.state.resu.name}
      image={this.state.resu.mage}
      description={this.state.resu.description}
      github={this.state.resu.github}
    />
  ))}
  </Wrapper>

)
}

}

export default Portfolio;
